import React, { Component } from 'react'
import ReactHtmlParser from 'react-html-parser';
import Prices from 'components/Prices/index.js'
import { ProdImage } from 'pages/Prods/Prod/parts/Image'
import TrustabilityBadges from "components/TrustabilityBadges/"
import {i18nContext} from 'contexts/i18n';

export default  class Infos extends Component {
  static contextType = i18nContext;
  render(){
    const { prod } = this.props
    return (
      <React.Fragment>
        <span itemProp="price">{prod.price}</span>
        <span itemProp="priceCurrency">{prod.currency}</span>
      </React.Fragment>
    )
  }
}

Infos.contextType = i18nContext
