import React, { Component } from 'react'
import { connect } from "react-redux";
import Link from "components/Link/";
import { getPath } from "helpers/getPath"
import {i18nContext} from 'contexts/i18n';
import { fetchCategs } from 'state/actions'

class Nav extends Component {
  static contextType = i18nContext;
  componentDidMount () {
    this.props.dispatch(fetchCategs())
  }
  closeNav = () => { this.props.statusNavigation('hide') }
  render(){
    let i18n = this.context;
    const { listClass, elemsClass, linksClass, categories, loading } = this.props
    let path = getPath( i18n.lang )
    // se le categorie sono state caricate, allora mappale e crea l'array per la lista del menu
    let menuElems = loading ? [] : categories.map((cat,i) => (
                                    {link: path.prods+'/'+cat.name+'/'+cat.id,
                                      text: cat.name,
                                      state: cat.id
                                    }
                                  ))

    const menuElem = menuElems.map((elem,index) => (
      <li className={elemsClass} key={index}>
        <Link className={linksClass} to={ {pathname: elem.link, state:  elem.state } } onClick={this.closeNav} >
          {elem.text}
        </Link>
      </li>
    ))
    return(
      <ul className={'t-nav '+listClass}>
        <li className="pt-1 pb-2 px-3 text-uppercase">
          <strong>{i18n.products}</strong>
        </li>
        {menuElem}
      </ul>
    )
  }
}
Nav.contextType = i18nContext
const mapStateToProps = (state) => {
  const { categories } = state
  return {
    loading: categories.loading,
    categories: categories.list
  }
}
export default connect(mapStateToProps)( Nav );
