import React, { Component } from 'react'

class RemoveBtn extends Component {
  removeItem = () => {
    const { prod, index, removeItem } = this.props
    removeItem(prod, index)
  }
  render() {
    const { className } = this.props
    let className_f = className ? className : ""
    return (
      <h3 onClick={this.removeItem}
          className={"p-cart__action cursor-pointer text-dark "+className_f}>
        &times;
      </h3>
    )
  }
}
export default RemoveBtn
