import React, { Component } from 'react'
import { connect } from "react-redux"
import {i18nContext} from 'contexts/i18n';
import { getPath } from "helpers/getPath"
import withTemplate from 'templates/'
import { Full } from 'pages/Prods/Lists/Row'
import Link from "components/Link/";
import ModalUser from './parts/Modal'
import RemoveBtn from './parts/RemoveBtn'
import { removeFromCartByIndex } from 'state/actions'

class Cart extends Component {
  static contextType = i18nContext;
  constructor(props) {
    super(props)
    this.state = {
      isShowingModal: false,
      isShowingThanks: false,
    }
  }
  openModal = () => { this.setState({ isShowingModal: true }) }
  closeModal = () => { this.setState({ isShowingModal: false }) }
  sayThanks = () => { this.setState({ isShowingThanks: true }) }
  removeItem = ( prod, index ) => {
    const i18n = this.context
    // Finestra di conferma. Se si clicca ok, tornerà TRUE.
    if ( window.confirm(i18n.actions.confirm_delete) === true ) {
      this.props.dispatch( removeFromCartByIndex(prod,index) )
    }
  }
  render(){
    let path = getPath()
    let i18n = this.context;
    const { cart } = this.props
    const cartIsFull = (cart && !cart.submitted && cart.list && cart.list.length > 0 )
    const Full = <CartFull sayThanks={this.sayThanks}
                    enableModal={true} cart={cart}
                    openModal={this.openModal}
                    closeModal={this.closeModal}
                    isShowingModal={this.state.isShowingModal}
                    removeItem={this.removeItem}
                    i18n={i18n}
                  />
    const Empty = <CartEmpty thanks={this.state.isShowingThanks} i18n={i18n} path={path} />
    return (
      <div className="p-cart">
        { cartIsFull ? Full : Empty }
      </div>
    )
  }
}
Cart.contextType = i18nContext
const mapStateToProps = (state) => {
  const { cart, user } = state
  return { cart, user }
}
export default connect(mapStateToProps)( withTemplate(Cart) );

// - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
// PRIVATE

const CartEmpty = (props) => {
  const { i18n, path, thanks } = props
  return (
    <div className="text-center">
      { thanks
        ? <div>
            <h3>{i18n.info.thanks_after_order}</h3>
            <p>{i18n.info.more_than_one_call}</p>
          </div>
        : <div>
            <p>{i18n.cart.empty}</p>
          </div>
      }
      <p><Link to={path.home} className="btn btn-success">{i18n.actions.back_to_home}</Link></p>
    </div>
  )
}

const CartFull = (props) => {
  const { cart, openModal, closeModal, isShowingModal, i18n, sayThanks, removeItem } = props
  const Item = Full
  return (
    <div className="row">
      <div className="col-12">
        <h2>{i18n.cart.title}</h2> <hr />
      </div>
      <div className="col-12 pb-3">
        { cart.list.map((prod,index) => (
            <Item prod={prod} key={index}>
              <RemoveBtn prod={prod}
                         key={index}
                         index={index}
                         i18n={i18n}
                         removeItem={removeItem}
                         />
            </Item>
         )) }
      </div>
      <div className="col-12 pb-3 text-center">
        <ModalUser
          open={openModal}
          close={closeModal}
          sayThanks={sayThanks}
          isShowingModal={isShowingModal}
          i18n={i18n}
          />
      </div>
      <div className="col-12 pt-3">
        <div className="alert alert-secondary">
          {i18n.info.more_than_one_call}
        </div>
      </div>
    </div>
  )
}
