import './style.scss';
import React, { Component } from 'react'
import Link from "components/Link/";
import { LazyLoadImage, trackWindowScroll } from 'react-lazy-load-image-component';
import { f_ifexist } from "helpers/formatter"

// PRIVATES (non esportati)

class TagImg extends Component {
  render(){
    const { src, alt, className, imgStyleExternal, notLazy } = this.props
    const imgStyle = "c-image c-image--tag img-fluid"+f_ifexist(className)+f_ifexist(imgStyleExternal)
    const img = notLazy ? <img className={imgStyle} alt={alt} src={src} />
                        : <LazyLoadImage className={imgStyle+"c-image--lazyed"} alt={alt} src={src} />
    return (
      <React.Fragment>{img}</React.Fragment>
    )
  }
}

class BkgImg extends Component {
  render(){
    let { src, alt, className, bkgsize, ratio } = this.props
    // calcolo il ratio (se presente la prop). Usare frazioni. (es: 2/3, 4/5...)
    let n = ratio && ratio.split("/");
    let ratioPercent = ( n && ! isNaN(n[0]) && ! isNaN(n[1]) ) ? ((n[0]/n[1])*100)+'%' : false
    let imgProps = {
      alt,
      className: ' c-image--bkg'+f_ifexist(className),
      style: {
        ...this.props.style,
        backgroundImage: "url("+src+")",
        backgroundSize: bkgsize ? bkgsize : 'contain',
        paddingTop : ratioPercent ? ratioPercent : '100%'
      }
    }
    return (
      <div {...imgProps}></div>
    )
  }
}

// PUBLIC

class Image extends Component {
  render() {
    const { src, alt, imgStyle, link, wrapperStyle, containerStyle, imgStyleExternal, useBkg } = this.props
    let imgProps = {
      src,
      alt: (!!alt ? alt : ''),
      className: 'c-image '+f_ifexist(imgStyle),
      imgStyleExternal: 'c-image__external'+f_ifexist(imgStyleExternal),
    }
    // valuto se stampare l'immagine in tag o in background
    let imageObj = (!!useBkg) ? <BkgImg {...imgProps} />
                              : <TagImg {...imgProps} />

    let result = link ? <Link to={link} className={"c-image--link"+f_ifexist(imgStyleExternal)}> {imageObj} </Link> : imageObj
    // valuto se stampare l'immagine o il link che wrappa l'immagine
    return (
      <React.Fragment>
        <div className={'c-image__container' + f_ifexist(containerStyle)}>
          <div className={'c-image__wrapper' + f_ifexist(wrapperStyle)}>{result}</div>
        </div>
      </React.Fragment>
    )
  }
}

export default trackWindowScroll(Image);
