import React, { Component } from 'react'
import {i18nContext} from 'contexts/i18n';
import Link from "components/Link/";
import { getPath, getCategLink } from "helpers/getPath"
import { f_ucfirst } from "helpers/formatter"

class Breadcrumb extends Component {
  static contextType = i18nContext;
  render() {
    let i18n = this.context;
    // NOTE: pattern deve essere un array di oggetti così composto:
    // [{name:'nomelink',link:'urllink'}, {name:'nomelink',link:'urllink'}];
    const { pattern, destination } = this.props
    let path = getPath( i18n.lang )
    const linkList = pattern.map((p,i) => <Link className="px-2" to={p.link} key={i}>{f_ucfirst(p.name)}</Link>)
    console.log( pattern, destination );
    return (
      <div className="t-breadcrumb">
      <div className="breadcrumb">
        <div className="container">
          {/*<Link className="px-2" to={path.home}>Home</Link>*/}
          {linkList}
          <span className="px-2">{destination}</span>
        </div>
      </div>
      </div>
    )
  }
}

export default Breadcrumb;
