import React, { Component } from 'react'
import {i18nContext} from 'contexts/i18n';
import { getPath } from "helpers/getPath"
import withTemplate from 'templates/'
import { Full } from 'pages/Prods/Lists/Row'
import Actions from 'pages/Cart/actions'
import Form from 'pages/Cart/form'
import Link from "components/Link/";
import Modal from 'components/Modal/';

const FromUser = (props) => {
  return ( <Form /> )
}

const ModalUser = (props) => {
  return (
    <React.Fragment>
      <Modal show={props.isShowing}
             close={props.close}
             title={props.i18n.order_now_cta}>
          {props.isShowing && <FromUser />}
      </Modal>
      <button className="btn btn-success btn-lg d-inline-block"
              onClick={props.open} >
        {props.i18n.order_now_cta}
      </button>
    </React.Fragment>
  )
}

const Prods = (props) => {
  return (
    props.cart.list.map((prod,index) => (<Item prod={prod} key={index} index={index}>
                                                                    <Actions prod={prod} key={index} index={index} />
                                                                  </Item>))
                                                                 )
}

class Cart extends Component {
  static contextType = i18nContext;
  constructor(props) {
    super(props)
    this.state = {
      isShowing: false
    }
  }
  openModal = () => {
    this.setState({ isShowing: true });
  }
  closeModal = () => {
    this.setState({ isShowing: false });
  }
  render(){
    let path = getPath()
    let i18n = this.context;
    const Item = Full
    const { cart } = this.props
    const cartIsFull = (cart && cart.list && cart.list.length > 0 )
    const prodotti = cartIsFull && <Prods cart={cart} />

    const fullCart = <React.Fragment>
                       <div className="row">
                          <div className="col-12"><h2>{i18n.cart}</h2><hr /></div>
                          <div className="col-12 pb-3"> <Prods cart={cart} /></div>
                          <div className="col-12 pb-3 text-center">
                            <ModalUser open={this.openModal} close={this.closeModal} isShowing={this.state.isShowing} i18n={i18n} />
                          </div>
                          <div className="col-12 pt-3">
                            <div className="alert alert-secondary">
                              {i18n.alert_more_than_one_call}
                            </div>
                          </div>
                        </div>
                      </React.Fragment>
    const emptyCart = <div className="text-center">
                        <p>{i18n.empty_cart}</p>
                        <p><Link to={path.home} className="btn btn-success">{i18n.home}</Link></p>
                      </div>
    return (
      <div className="p-cart">
        { cartIsFull ? fullCart : emptyCart}
      </div>
    )
  }
}
Cart.contextType = i18nContext
export default withTemplate(Cart)
