import React, { Component } from 'react'
import { connect } from "react-redux"
import Nav from './Nav'

import Link from "components/Link/";


class Header extends Component {
  toggleDropdown = () => { this.props.statusNavigation('toggle') }
  render() {
    const { showNavigation, statusNavigation } = this.props
    return (
      <React.Fragment>

        <li className="nav-item dropdown">
          <Link className="nav-link dropdown-toggle" to="#" onClick={this.toggleDropdown}>
            <span className="navbar-toggler-icon"></span>
          </Link>
          <div className={"dropdown-menu "+( showNavigation ? 'show' : '' )} aria-labelledby="navbarDropdownMenuLink">
            <Nav
              listClass="navbar-nav ml-auto mr-2"
              elemsClass="nav-item"
              linksClass="nav-link"
              statusNavigation={statusNavigation}
            />
          </div>
        </li>

        {/*<div onClick={this.toggleDropdown}
             className="navbar-toggler"
             type="button"
             data-toggle="collapse"
             data-target="#navbarSupportedContent"
             aria-controls="navbarSupportedContent"
             aria-expanded="false"
             aria-label="Toggle navigation"
            >
          <span className="navbar-toggler-icon">
          </span>
        </div>
        <div className={"collapse navbar-collapse "+( showNavigation ? 'show' : '' )} id="navbarSupportedContent">
          <Nav
            listClass="navbar-nav ml-auto mr-2"
            elemsClass="nav-item"
            linksClass="nav-link"
            statusNavigation={statusNavigation}
          />
        </div>*/}
      </React.Fragment>
    )
  }
}

const mapStateToProps = (state) => {
  const { cart } = state
  return {
    cart
  }
}
export default connect(mapStateToProps)( Header );
