import React, { Component } from 'react'
import { connect } from "react-redux";
import {i18nContext} from 'contexts/i18n';
import { removeFromCart } from 'state/actions'
import { indexProdInList } from 'helpers/object_and_json.js'
import Icon from 'components/Icon/'

class AsideProd extends Component {
  static contextType = i18nContext;
  removeItemFromCart = () => {
    const { prod } = this.props
    this.props.dispatch( removeFromCart(prod) )
  }
  render(){
  let i18n = this.context;
  const { prod } = this.props
  const { openModalHandler, addToCart, cart } = this.props
  return (
    <React.Fragment>
        <button className="mt-3 btn btn-block btn-lg btn-success"
                onClick={openModalHandler}> {i18n.order_now_cta}
        </button>
        { indexProdInList(prod,cart.list) >= 0
          ?  <span className="btn btn-text btn-block mx-auto mt-2"
                   onClick={this.removeItemFromCart}
                   style={{'maxWidth':'400px'}} >
               <Icon icon={'icon-minus-circle'}
                     text={i18n.remove_from_cart} />
             </span>
          :  <span className="btn btn-text btn-block mx-auto mt-2"
                   onClick={addToCart}
                   style={{'maxWidth':'400px'}} >
               <Icon icon={'icon-plus-circle'}
                     text={i18n.add_to_cart} />
             </span> }
      </React.Fragment>
    )
  }
}
AsideProd.contextType = i18nContext
const mapStateToProps = (state) => {
  const { currency, prods, cart } = state
  return {
    currency,
    loading: prods.loading,
    error: prods.error,
    cart
  }
}
export default connect(mapStateToProps)( AsideProd );
