export default function cart(state, action) {

  let a = action.payload
  let p = a ? a.list : false
  let ls = state.list ? state.list : []
  let submForList =  ls && ls.length < 0 ? true : false
  let prod = p ? { id:p.id, network:p.network, code:p.code, name:p.name,
                   slug:p.slug, lang:p.lang, country:p.country,
                   currency:p.currency, price:p.price, oldprice:p.oldprice }
               : false

  switch (action.type) {
      case 'SET_CART':
          return {
              ...state,
              list:  a.list,
              submitted: submForList
          }
      case 'SET_SUBMIT_STATUS':
          return {
              ...state,
              submitted:  a.submitted,
          }
      case 'ADD_TO_CART':
        // controllo se l'ID prodotto c'è già in lista
        let alreadyInList = false
        for (var i = 0; i < ls.length; i++) {
          if( ls[i].id === prod.id ) {
            alreadyInList = true
          }
        }
        if( alreadyInList ) {
          // Se c'è non fare nulla (per ora non gestiamo la QTY)
          return state;
        } else {
          // Se NON c'è aggiungilo alla lista (o crea lista nuova se carrello è vuoto)
          let firstProd = [prod]
          // https://stackoverflow.com/questions/41364383/remove-duplicates-form-an-array/41364495
          let prodInList = [...new Set([...ls, prod].map(a => a))]
          return {
              ...state,
              list : ls ? prodInList : firstProd,
              submitted: submForList
          }
        }
      case 'REMOVE_FORM_CART':
        let prodExist = 0;
        // ciclo sullo stato - lista prodotti 
        for (i = 0; i < ls.length; i++) {
          if( ls[i].id === a.list.id ) {
            prodExist = i+1 // aggiungo 1 per non avere un indixe a zero
          }
        }

        console.log(ls, a.list, prodExist);
        return state;

      case 'REMOVE_FORM_CART_LIST':
        if( ls && ls.length > 0 && a.index !== undefined ) {
          let newArray = ls // copio l'array in un'altra cella di memoria
          newArray.splice(a.index,1) // modifico l'array
          return {
              ...state,
              list : newArray,
              submitted: submForList
          }
        } else {
          return state;
        }

      default:
          return state;
    }
}
