import React from 'react'
import { f_ifexist } from "helpers/formatter"

export default (props) => {
  const wrapperStyle = " d-flex align-items-center "+f_ifexist(props.iconWrapperStyle)
  const style = " d-flex align-items-center "+f_ifexist(props.iconStyle)
  const textStyle = "c-icon-text ml-2 "+f_ifexist(props.textStyle)
  const Icon = <i className={props.icon}></i>
  const Text = <div className={"c-icon__wrapper "+wrapperStyle}>
                  <div className={"c-icon "+style}> {Icon} </div>
                  {props.text && <div className={textStyle}> {props.text} </div>}
                </div>
  return (
    <React.Fragment> {Text ? Text : Icon} </React.Fragment>
  )
}
