import React, { Component } from 'react'
import { connect } from "react-redux"
import Icon from 'components/Icon/'
//import Item from "pages/Prods/Lists/MiniRow"
//import Link from "components/Link/";
//import ProdLink from "pages/Prods/Prod/parts/Link"

class Header extends Component {
  toggleDropdown = () => { this.props.statusCart('toggle') }
  render() {
    const { showCart, cart } = this.props
    const prodotti = cart.list.map((prod) => (
      <li className="nav-link" key={prod.id}>
        {prod.name}
        {/*<Item prod={prod} key={prod.id} />*/}
      </li>))
    return (
      <React.Fragment>

      <span class="nav-item dropdown">
        <div className="text-light mr-3" onClick={this.toggleDropdown} >
          <Icon icon={'icon-shopping-cart'} text={ (cart.list && cart.list.length > 0) && <span className="badge badge-success">{cart.list.length}</span>}/>
        </div>
        <ul class={"dropdown-menu "+( showCart ? 'show' : '' )} role="menu">
          {prodotti}
        </ul>
      </span>


      {/*  <div className="text-light mr-3" onClick={this.toggleDropdown} >
          <Icon icon={'icon-shopping-cart'} text={ (cart.list && cart.list.length > 0) && <span className="badge badge-success">{cart.list.length}</span>}/>
        </div>
        <ul className={"collapse navbar-collapse "+( showCart ? 'show' : '' )} id="navbarSupportedContent">
          {prodotti}
        </ul>*/}
      </React.Fragment>
    )
  }
}
const mapStateToProps = (state) => {
  const { cart } = state
  return { cart }
}
export default connect(mapStateToProps)( Header );
