      <div className="container">
        {titleBox}
        {childBox}
        {ctaBox}
      </div>
      <div className='c-banner__bkg img-bkg'
      style={{backgroundImage:"url("+imgUrl+")"}}
      ></div>
    </div>
  )
}

export default Banner
