import './style.scss';
import React, { Component } from 'react'
import { connect } from "react-redux"
import Link from "components/Link/";
import Navigation from './Navigation'
import { getPath } from "helpers/getPath"
import {i18nContext} from 'contexts/i18n'
import Icon from 'components/Icon/'

class Header extends Component {
  constructor(props) {
    super(props)
    this.state = {
      showNavigation: false,
    }
  }
  statusNavigation = (status) => {
    let finalStatus = (status==='show') ? true : (status==='hide') ? false : !this.state.showNavigation
    this.setState({ showNavigation: finalStatus })
  }
  static contextType = i18nContext;
  render() {
    const { REACT_APP_NAME } = process.env
    let i18n = this.context;
    let path = getPath( i18n.lang )
    return (
      <nav className="t-header navbar sticky-top XXX-navbar-expand-lg navbar-dark bg-dark">
        <div className="container pl-3 pr-2 flex-nowrap">
          <div className="navbar-brand px-0 mr-0 font-weight-normal w-100 flex-grow-1">
          <Link to={path.lang} className="d-inline-block">
            <Icon icon={'icon-fire'} iconWrapperStyle={'mr-1 w-100'} textStyle={'ellipsed'} text={REACT_APP_NAME} />
            </Link>
            </div>
          <Navigation showMenu={this.state.showNavigation}  statusMenu={this.statusNavigation}  />
        </div>
      </nav>
    )
  }
}
Header.contextType = i18nContext

const mapStateToProps = (state) => {
  const { cart } = state
  return {
    cart
  }
}
export default connect(mapStateToProps)( Header );
