import React, { Component } from 'react'
import { connect } from "react-redux"
import MenuIcon from "templates/parts/Header/MenuIcon.js";
import Link from "components/Link/";
import List from './List'
import FullCart from './FullCart.js'
import { getPath } from "helpers/getPath"
import {i18nContext} from 'contexts/i18n';

class Cart extends Component {
  toggleDropdown = () => { this.props.statusMenu('toggle') }
  closeDropdown = () => { this.props.statusMenu('close') }
  static contextType = i18nContext;
  render() {
    let i18n = this.context
    let path = getPath(i18n.lang)
    const { showMenu, cart } = this.props
    const fullCart = <FullCart>
                      <List cart={cart} />
                      </FullCart>
    const emptyCart = <span>{i18n.empty_cart}</span>
    return (
      <div className="nav-item dropdown">
        <div className="text-light " onClick={this.toggleDropdown} >
          <MenuIcon icon={'icon-shopping-cart'} toggleDropdown={this.toggleDropdown} showMenu={showMenu}>
            { (cart.list && cart.list.length > 0)
                    && <span className="badge badge-success">{cart.list.length}</span>}
          </MenuIcon>
        </div>
        <div className={"dropdown-menu p-3 "+( showMenu ? 'show' : '' )} role="menu">
          {cart.list.length > 0 ? fullCart : emptyCart}
        </div>
      </div>
    )
  }
}
Cart.contextType = i18nContext
const mapStateToProps = (state) => {
  const { cart } = state
  return { cart }
}
export default connect(mapStateToProps)( Cart );
