import React, { Component } from 'react'
import {i18nContext} from 'contexts/i18n';

class Footer extends Component {
  static contextType = i18nContext;
  render() {
    let i18n = this.context;
    const { REACT_APP_NAME, REACT_APP_FORM } = process.env
    const year = new Date().getFullYear();
    return (
      <footer className="t-footer">
        <div className="text-center my-2">
          {year} &copy; {REACT_APP_NAME}
          <span className="px-2">|</span>
          <a href={REACT_APP_FORM+'info.php?pag=privacy&lang='+i18n.lang} target="_blank" rel="noopener noreferrer">Privacy</a>
          <span className="px-2">|</span>
          <a href={REACT_APP_FORM+'info.php?pag=terms&lang='+i18n.lang} target="_blank" rel="noopener noreferrer">Terms</a>
        </div>
      </footer>
    )
  }
}

Footer.contextType = i18nContext

export default Footer;
