import React, { Component } from 'react'
import { f_ifexist } from "helpers/formatter"
import {i18nContext} from 'contexts/i18n';

class Input extends Component {
  static contextType = i18nContext;
  render(){
    let i18n = this.context;
    let { form } = i18n
    let { label, placeholder, name, className, type, id } = this.props
    const fieldName = placeholder ? placeholder : label
                            ? label : form[name]
                            ? form[name] : name;
    return (
      <div className="c-input__wrapper form-group">
        {label && <label className="c-label">{fieldName}</label>}
        <input {...this.props}
               className={"c-input form-control"+f_ifexist(className)}
               type={type?type:"text"}
               placeholder={fieldName}
               id={id?id:name} />
      </div>
    )
  }
}

const Form = (props) => {
  return (
    <form {...props} className={"c-form"+f_ifexist(props.className)} >{props.children}</form>
  )
}

const Submit = (props) => {
  return (
    <div className="c-submit form-group mt-4">
      <input {...props}
             type="submit"
             value="Submit"
             className={"btn btn-success btn-lg d-block float-none m-auto"
                        + f_ifexist(props.className)} />
    </div>
  )
}

Input.contextType = i18nContext
export { Form, Input, Submit }
