// Store GCLID in Cookie and Send to Hubspot
// Pass Tracking Parameters to a Form on Another Page Using GTM
// http://zackphilipps.com/store-gclid-cookie-send-to-hubspot/

import { setTracker } from 'state/actions'
import { getTracker } from "helpers/querystring"
import { parseIfJsonString } from "helpers/object_and_json"
import { fetchCart } from 'state/actions'
import { f_trimall } from "helpers/formatter"

// REACT_APP_NAME // string = string.replace(/[^a-zA-Z0-9]/g, '');

export function getCookie(name) {
  var value = '; ' + document.cookie;
  var parts = value.split('; ' + cookiePrefixer(name) + '=');
  if (parts.length === 2)
    return parts.pop().split(';').shift();
}

export function setCookie(name, value, days) {
  if( value ) {
    let setDays = ( !isNaN(days) ) ? days : 30;
    let strValue = ( typeof value === 'object' ) ? JSON.stringify(value) : value
    var date = new Date();
    // setta la scadenza in giorni
    date.setTime(date.getTime() + (setDays * 24 * 60 * 60 * 1000));
    var expires = '; expires=' + date.toGMTString();
    document.cookie = cookiePrefixer(name) + '=' + strValue + expires + ';path=/';
  }
}

// Qui vengono inizializzati i cookies
export function cookiesInit(componentProps) {
  setStateFromCookie_tracker(componentProps)
  setStateFromCookie('cart',fetchCart,'list',componentProps)
  //setStateFromCookie('user',fetchUser,'list',componentProps)
  //setStateFromCookie_abTest(componentProps) // rifare funzione usando setStateFromCookie()
}

// - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
// PRIVATES


function setStateFromCookie(cookieName,actionToDispatch,valueInObj,componentProps) {
  const obj = parseIfJsonString( getCookie(cookieName) )
  if( obj[valueInObj] ) componentProps.dispatch( actionToDispatch(obj[valueInObj]) )
  if( !valueInObj ) componentProps.dispatch( actionToDispatch(obj) )
}


// Imposta il prefisso dei nomi dei cookie in set e get
function cookiePrefixer(name) {
  return f_trimall(process.env.REACT_APP_NAME).toLowerCase() + '_' + name;
}

function setStateFromCookie_tracker(componentProps) {
  // recupero la querystring
  const querystring = require('query-string').parse(componentProps.location.search);
  // setto il trackid e descrivo se è google o bing (usando l'helper apposito)
  const tracker = getTracker(querystring);
  // se c'è il trackid nella querystring, lo setto nel cookie
  if(tracker.trackid) setCookie('tracker',tracker)


  console.log('tracker:',tracker, getCookie('tracker'));



  //const myCookie = parseIfJsonString( getCookie('tracker') )
  // salvo nello stato globale il cookie per poterlo passare alla form
  //if( myCookie && myCookie.trackid ) componentProps.dispatch( setTracker( myCookie ) )

    setStateFromCookie('tracker',setTracker,false,componentProps)
}

