import React, { Component } from 'react'
import Aside from 'components/Aside'
import PricesBox from 'components/Prices'
import TrustabilityBadges from "components/TrustabilityBadges/"
import {i18nContext} from 'contexts/i18n';
import Form from './Form/'
import Actions from 'pages/Prods/Prod/parts/Actions/index.js'

export default class AsideProd extends Component {
  static contextType = i18nContext;
  render(){
  let i18n = this.context;
  const { prod, showForm, openModalHandler, addToCart, removeFromCart } = this.props
  const btn = <Actions openModalHandler={openModalHandler}
                       addToCart={addToCart}
                       removeFromCart={removeFromCart}
                       prod={prod} />
  const form = <div className="pt-4">
                      <Form prod={prod} />
                  </div>
  return (
        <Aside>
            <h4 className="mb-2">{i18n.actions.order_now_cta} {prod.name}</h4>
            <hr />
            <PricesBox prod={prod}
                    colLabelStyle="col-7 text-left "
                    colPriceStyle="col text-right " />
            { showForm ? form : btn }
            <hr />
            <TrustabilityBadges itemStyle={'is-icons-empty'} />
        </Aside>
      )
    }
}
AsideProd.contextType = i18nContext
