import './style.scss';
import React, { Component } from 'react';
import { connect } from "react-redux";
import Banner from 'components/Banner/'
import Cat from 'components/Banner/Category'
import { getPath } from "helpers/getPath"
import withTemplate from 'templates/'
import {i18nContext} from 'contexts/i18n';
import { fetchCategs } from 'state/actions'

class Home extends Component {
  static contextType = i18nContext;

  componentDidMount () {
    this.props.dispatch(fetchCategs())
  }

  render() {
    let i18n = this.context;
    const { categories, loading } = this.props
    let path = getPath(i18n.lang)

    let menuElems = loading ? [] : categories.map((cat,i) => (
                                    {link: path.prods+'/'+cat.name+'/'+cat.id,
                                      name: cat.name,
                                      id: cat.id
                                    }
                                  ))

    const menuElem = menuElems.map((elem,index) => (
      <Cat id={"cat_"+elem.id}
           title={elem.name}
           link={elem.link}
           align={"center"}
           img={elem.id}
           key={index}
      />
    ))

    return (
      <div className="p-home xd-flex w-100">


      <Banner id={"fioriInTesta"}
                  title={"Sbocciano offerte, quest'estate!"}
                  cta={"Trova la tua!"}
                  link={path.prods}
                  align={"center"}
                  img={"fioriInTesta"}
          />
          <Banner id={"bocca"}
                  title={"Occasioni sulla bocca di tutti"}
                  cta={"Provale..."}
                  link={path.prods}
                  align={"center"}
                  img={"bocca"}
          />
          <Banner id={"fogliaOcchio"}
                  title={"Vuoi vedere prodotti imperdibili?"}
                  cta={"Vedi catalogo"}
                  link={path.prods}
                  align={"center"}
                  img={"fogliaOcchio"}
          />
          <Banner id={"agrumata"}
                  title={"Circondati di sconti"}
                  cta={"Scoprili subito!"}
                  link={path.prods}
                  align={"center"}
                  img={"agrumata"}
          />
      </div>
    )
  }
}

Home.contextType = i18nContext
//export default withTemplate(Home)

Home.contextType = i18nContext
const mapStateToProps = (state) => {
  const { categories } = state
  return {
    loading: categories.loading,
    categories: categories.list
  }
}
export default connect(mapStateToProps)( withTemplate(Home) );
