import React, { Component } from 'react'
import { connect } from "react-redux"
import {i18nContext} from 'contexts/i18n';
import { fetchUser } from 'state/actions'

import { Input } from "components/Form/";

class Form extends Component {
  static contextType = i18nContext;

  constructor(props) {
    super(props)
    this.state = {
      info: this.props.user.info
    }
  }

  handleChange = (event) => {
    const target = event.target;
    const value =
      target.type === 'checkbox'
        ? target.checked
        : target.value;
    const name = target.name;

    this.setState({ info:{
      ...this.state.info,
      [name]: value,
    }});
  }

  handleSubmit = (event) => {
    console.log('E\' stato inserito un valore: ', this.state.info);
    event.preventDefault();
    this.props.dispatch( fetchUser( this.state.info ) )
  }

  render(){
    const localInfo = this.state.info
    const fields = ['name','phone','address','city','zipcode','email']
    const inputs = fields.map((field, i) => ( <Input key={i}
                                                     defaultValue={localInfo[field]}
                                                     name={field}
                                                     onChange={this.handleChange} /> ));

    return (
      <React.Fragment>
        METTERE QUI LA FORM

        <form onSubmit={this.handleSubmit}>
          {inputs}
          <input type="submit" value="Submit" className="btn btn-success" />
        </form>

      </React.Fragment>
    )
  }
}

Form.contextType = i18nContext
const mapStateToProps = (state) => {
  const { cart, user } = state
  return { cart, user }
}
export default connect(mapStateToProps)( Form );
