import React, { Component } from 'react'
import { connect } from "react-redux"
import MenuIcon from "templates/parts/Header/MenuIcon.js";
import List from './List'
import Actions from './Actions.js'
import {i18nContext} from 'contexts/i18n';

class Cart extends Component {
  toggleDropdown = () => { this.props.statusMenu('toggle') }
  closeDropdown = () => { this.props.statusMenu('close') }
  static contextType = i18nContext;
  render() {
    let i18n = this.context
    const { showMenu, cart } = this.props
    const itemsNum = cart.list ? cart.list.length : 0;
    return (
      <div className="nav-item dropdown">
        <div className="text-light " onClick={this.toggleDropdown}>
          <MenuIcon icon={'icon-shopping-cart'}
                    toggleDropdown={this.toggleDropdown}
                    showMenu={showMenu}
                    badgeNumber={itemsNum} />
        </div>
        <div className={"dropdown-menu p-3 "+( showMenu ? 'show' : '' )} role="menu">
          {itemsNum > 0
            ? <React.Fragment>
                <List cart={cart} />
                <Actions closeDropdown={this.closeDropdown} />
              </React.Fragment>
            : i18n.cart.empty
          }
        </div>
      </div>
    )
  }
}
Cart.contextType = i18nContext
const mapStateToProps = (state) => {
  const { cart } = state
  return { cart }
}
export default connect(mapStateToProps)( Cart );
