import React from 'react'
import Item from './Item'

export default (props) => {
    const badges = [
      {
        text: 'Spedizione Rapida Gratuita',
        //icon: 'fa-shipping-fast'
        icon: 'icon-truck'
      },{
        text: 'Soddisfatti o rimborsati',
        //icon: 'fa-handshake'
        icon: 'icon-handshake'
      },{
        text: 'Pagamento alla consegna',
        //icon: 'fa-hand-holding-usd'
        icon: 'icon-banknote'
      },{
        text: 'Miglior prezzo garantito',
        //icon: 'fa-clipboard-check'
        icon: 'icon-star'
      }
    ]
    const Items = (badges) &&
                    badges.map((badge, i) => (
                      <Item badge={badge} key={i} />
                    ));
    return (
      <section className={"c-trustability-badge__wrapper "+props.itemStyle+" row"}>
        {Items}
      </section>
    )
}
