import React, { Component } from 'react'
import {i18nContext} from 'contexts/i18n';
import { getPath } from "helpers/getPath"
import withTemplate from 'templates/'
import { Full } from 'pages/Prods/Lists/Row'
import Actions from 'pages/Cart/actions'
import Form from 'pages/Cart/form'
import Link from "components/Link/";
import Modal from 'components/Modal/';

class Cart extends Component {
  static contextType = i18nContext;
  constructor(props) {
    super(props)
    this.state = {
      isShowing: false
    }
  }
  openModal = () => {
    this.setState({ isShowing: true });
  }
  closeModal = () => {
    this.setState({ isShowing: false });
  }
  render(){
    let path = getPath()
    let i18n = this.context;
    const { cart } = this.props
    const cartIsFull = (cart && cart.list && cart.list.length > 0 )

    return (
      <div className="p-cart">
        { cartIsFull
          ? <CartFull enableModal={true} cart={cart} openModal={this.openModal} closeModal={this.closeModal} isShowing={this.state.isShowing} i18n={i18n} />
          : <CartEmpty i18n={i18n} path={path} /> }
      </div>
    )
  }
}
Cart.contextType = i18nContext
export default withTemplate(Cart)

// - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
// PRIVATE

const CartEmpty = (props) => {
  const { i18n, path } = props
  return (
    <div className="text-center">
      <p>{i18n.empty_cart}</p>
      <p><Link to={path.home} className="btn btn-success">{i18n.home}</Link></p>
    </div>
  )
}

const CartFull = (props) => {
  const { enableModal, cart, openModal, closeModal, isShowing, i18n } = props
  return (
    <div className="row">
      <div className="col-12">
        <h2>{i18n.cart}</h2> <hr />
      </div>
      <div className="col-12 pb-3">
        <ProdsList cart={cart} />
      </div>
      <div className="col-12 pb-3 text-center">
        { enableModal
          ? <ModalUser open={openModal} close={closeModal} isShowing={isShowing} i18n={i18n} />
          : <FromUser />}
      </div>
      <div className="col-12 pt-3">
        <div className="alert alert-secondary">
          {i18n.alert_more_than_one_call}
        </div>
      </div>
    </div>
  )
}

const ProdsList = (props) => {
  const { cart } = props
  const Item = Full
  return (
    cart.list.map((prod,index) => (
      <Item prod={prod} key={index} index={index}>
        <Actions prod={prod} key={index} index={index} />
      </Item>
    ))
  )
}

const ModalUser = (props) => {
  const { isShowing, close, open, i18n } = props
  return (
    <React.Fragment>
      <Modal show={isShowing}
             close={close}
             title={i18n.order_now_cta}>
          {isShowing && props.children }
      </Modal>
      <button className="btn btn-success btn-lg d-inline-block"
              onClick={open} >
        {i18n.order_now_cta}
      </button>
    </React.Fragment>
  )
}

const FromUser = (props) => {
  return ( <Form /> )
}
