import React from 'react'

export default (props) => {
  const wrapperStyle = " d-flex align-items-center "+(props.iconWrapperStyle ? props.iconWrapperStyle : '')
  const style = " d-flex align-items-center "+(props.iconStyle ? props.iconStyle : '')
  const Icon = <i className={" fa "+props.icon}></i>
  const Text = <div className={"c-icon__wrapper "+style}>
                  <div className={"c-icon "+style}> {Icon} </div>
                  {props.text && <div className="c-icon-text ml-2"> {props.text} </div>}
                </div>
  return (
    <React.Fragment> {Text ? Text : Icon} </React.Fragment>
  )
}
