import React, { Component } from 'react'
import { connect } from "react-redux";
import { fetchCategs } from 'state/actions'
import { Mini } from 'pages/Prods/Lists/Row'

class Nav extends Component {
  componentDidMount () {
    this.props.dispatch(fetchCategs())
  }
  closeNav = () => { this.props.statusNavigation('hide') }
  render(){
    const { cart } = this.props
    const Item = Mini
    const prodotti = cart.list && cart.list.map((prod, index) => (
                       <Item prod={prod} key={index} index={index}></Item>
                     ))
    return(
      <React.Fragment>{prodotti}</React.Fragment>
    )
  }
}
const mapStateToProps = (state) => {
  const { categories } = state
  return {
    loading: categories.loading,
    categories: categories.list
  }
}
export default connect(mapStateToProps)( Nav );
