//import './style.scss';
import React, { Component } from 'react'
import Image from 'components/Image'
import { getProdLink, getProdLinkImg } from "helpers/getPath"
import {i18nContext} from 'contexts/i18n';
import { f_class } from "helpers/formatter"

class ProdImage extends Component {
  static contextType = i18nContext;
  render(){
    let i18n = this.context;
    const { prod, size, imgStyle, useLink, linkStyle, bkgsize, ratio, useBkg } = this.props
    // COMANDO DA TEMINALE per creare le thumb da lanciare in cartella imgs/prods:
    // for d in ./*/ ; do (cd "$d" && convert prod.png -resize 200x200  prod_thumb.png ); done
    const sizeName = size ? '_'+size : ''
    let prodImg = getProdLinkImg(i18n.lang,prod)+'/prod'+sizeName+'.png'
    let prodLink = useLink ? getProdLink(i18n.lang,prod) : false
    return(
      <Image src={prodImg}
             alt={prod.name}
             imgStyle={'c-prod-image '+imgStyle}
             link={prodLink}
             linkStyle={'c-prod-image__wrapper '+f_class(linkStyle)}
             useBkg={useBkg}
             bkgsize={bkgsize}
             ratio={ratio} />
    )
  }
}

ProdImage.contextType = i18nContext
export { ProdImage }

