import React, { Component } from 'react'
import {i18nContext} from 'contexts/i18n';
import Link from "components/Link/";
import { getPath, getCategLink } from "helpers/getPath"
import { f_ucfirst } from "helpers/formatter"

class Breadcrumb extends Component {
  static contextType = i18nContext;
  render() {
    let i18n = this.context;
    const { pattern, prod } = this.props
    let path = getPath( i18n.lang )

    let categLink = getCategLink(i18n.lang,prod).pathname

    console.log(path.home, prod, pattern, categLink)

    return (
      <div className="t-breadcrumb">
      <div className="breadcrumb">
        <div className="container">
          <Link to={path.home}>Home</Link> &raquo;
          <Link to={categLink}>{f_ucfirst(prod.category_name)}</Link> &raquo;
          FootFix
        </div>
      </div>
      </div>
    )
  }
}

export default Breadcrumb;
