import React, { Component } from 'react'
import Modal from 'components/Modal/';
import Form from 'pages/Cart/form'

class ModalUser extends Component {
  onCloseModal = () => {
    const { close, sayThanks } = this.props
    close()
    sayThanks()
  }
  render(){
    const { isShowingModal, open, close, i18n } = this.props
    return (
      <React.Fragment>
        <Modal show={isShowingModal}
               close={close}
               title={i18n.actions.order_now_cta}>
            {isShowingModal && <Form onSubmit={this.onCloseModal} /> }
        </Modal>
        <button className="btn btn-success btn-lg d-inline-block"
                onClick={open} >
          {i18n.actions.order_now_cta}
        </button>
      </React.Fragment>
    )
  }
}

export default ModalUser
