import React, { Component } from 'react'
import { connect } from "react-redux"
import {i18nContext} from 'contexts/i18n';
import { fetchUser } from 'state/actions'

import { Input } from "components/Form/";

class Form extends Component {
  static contextType = i18nContext;

  constructor(props) {
    super(props)
    this.state = {
      isShowing: true //false
    }
  }

  onSubmitForm = () => {
    this.props.dispatch( fetchUser( {name:'efadsf'} ) )
  }

  render(){
    //const i18n = this.context
    const { user } = this.props
    const { info } = user
    console.log( 'user.info in in', info );
    return (
      <React.Fragment>
        METTERE QUI LA FORM

          <Input defaultValue={info.name} name="name" />
          <Input defaultValue={info.phone} name="phone" />
          <Input defaultValue={info.address} name="address" />
          <Input defaultValue={info.city} name="city" />
          <Input defaultValue={info.zipcode} name="zipcode" />
          <Input defaultValue={info.email} name="email" />
          <button class="btn btn-success" onClick={this.onSubmitForm}>ciccio</button>

      </React.Fragment>
    )
  }
}

Form.contextType = i18nContext
const mapStateToProps = (state) => {
  const { cart, user } = state
  return { cart, user }
}
export default connect(mapStateToProps)( Form );
