export default function cart(state, action) {

  let a = action.payload
  let p = a ? a.list : false

  let prod = p ? { id:p.id, network:p.network, code:p.code, name:p.name,
                   slug:p.slug, lang:p.lang, country:p.country,
                   currency:p.currency, price:p.price, oldprice:p.oldprice }
               : false

  switch (action.type) {
      case 'SET_CART':
          return {
              ...state,
              list:  a.list,
          }
      case 'ADD_TO_CART':
        // https://stackoverflow.com/questions/41364383/remove-duplicates-form-an-array/41364495

        console.log()

        return state;

        // let prodInList = [...new Set([...state.list, prod].map(a => a))]
        // let firstProd = [prod]
        // return {
        //     ...state,
        //     list : state.list ? prodInList : firstProd
        // }
      case 'REMOVE_TO_CART':
        if( state.list.length > 0 && a.index !== undefined ) {
          let newArray = state.list // copio l'array in un'altra cella di memoria
          newArray.splice(a.index,1) // modifico l'array
          return {
              ...state,
              list : newArray
          }
        } else {
          return state;
        }

      default:
          return state;
    }
}
