import React, { Component } from 'react'
import {i18nContext} from 'contexts/i18n';
import withTemplate from 'templates/'
import Item from 'pages/Prods/Lists/Row'
import Actions from 'pages/Cart/actions'

class Cart extends Component {
  static contextType = i18nContext;
  render(){
    let i18n = this.context;
    const { cart } = this.props
    const prodotti = cart.list && cart.list.map((prod) => (<Item prod={prod} key={prod.id}><Actions prod={prod} /></Item>))
    return (
      <div className="p-cart">
        <h2>{i18n.cart}</h2>
        <hr />
        <button className="btn btn-success btn-right"></button>
        {prodotti}
      </div>
    )
  }
}
Cart.contextType = i18nContext
export default withTemplate(Cart)
