import React, { Component } from 'react'
import { connect } from "react-redux"
import Nav from './Nav'

class Header extends Component {
  toggleNav = () => { alert('t'); this.props.toggleNavigation('toggle') }
  render() {
    const { showNavigation, statusNavigation } = this.props
    return (
      <React.Fragment>
        <div onClick={this.toggleNav}
             className="navbar-toggler"
             type="button"
             data-toggle="collapse"
             data-target="#navbarSupportedContent"
             aria-controls="navbarSupportedContent"
             aria-expanded="false"
             aria-label="Toggle navigation"
            >
          <span className="navbar-toggler-icon">
          </span>
        </div>
        <div className={"collapse navbar-collapse "+( showNavigation ? 'show' : '' )} id="navbarSupportedContent">
          <Nav
            listClass="navbar-nav ml-auto mr-2"
            elemsClass="nav-item"
            linksClass="nav-link"
            statusNavigation={statusNavigation}
          />
        </div>
      </React.Fragment>
    )
  }
}

const mapStateToProps = (state) => {
  const { cart } = state
  return {
    cart
  }
}
export default connect(mapStateToProps)( Header );
