import { connect } from "react-redux"
import Icon from 'components/Icon/'

class Header extends Component {
  constructor(props) {
    super(props)
    this.state = {
      showCart: true
    }
  }
  render() {
    const { cart } = this.props
    return (
      <React.Fragment>
        <div className="text-light mr-3"
             onClick={this.toggleCart}
            >
          <Icon icon={'icon-shopping-cart'} text={<span className="badge badge-success">{cart.list.length}</span>}/>
        </div>
        <div className={"collapse navbar-collapse "+( this.state.showCart ? 'show' : '' )} id="navbarSupportedContent">
          x
        </div>
      </React.Fragment>
    )
  }
}
const mapStateToProps = (state) => {
  const { cart } = state
  return {
    cart
  }
}
export default connect(mapStateToProps)( Header );
