import React, { Component } from 'react'
import {i18nContext} from 'contexts/i18n';
import { getPath } from "helpers/getPath"
import withTemplate from 'templates/'
import { Full } from 'pages/Prods/Lists/Row'
import Actions from 'pages/Cart/actions'
import Form from 'pages/Cart/form'
import Link from "components/Link/";

import Modal from 'components/Modal/';

class Cart extends Component {
  static contextType = i18nContext;
  constructor(props) {
    super(props)
    this.state = {
      isShowing: true, //false
      resettedForm : false
    }
  }

  resetForm = () => {
    this.setState({ isShowing: true, resettedForm: false });
  }
  openModalHandler = () => {
    this.setState({ isShowing: true });
  }
  closeModalHandler = () => {
    this.setState({ isShowing: false });
  }
  render(){
    let path = getPath()
    let i18n = this.context;
    const Item = Full
    const { cart } = this.props
    const cartIsFull = (cart && cart.list && cart.list.length > 0 )
    const prodotti = cartIsFull && cart.list.map((prod,index) => (<Item prod={prod} key={index} index={index}>
                                                                   <Actions prod={prod} key={index} index={index} />
                                                                 </Item>))
    const button = <button className="btn btn-success btn-lg d-inline-block"
                           onClick={this.openModalHandler} >
                     {i18n.order_now_cta}
                   </button>

    const modal = <Modal show={this.state.isShowing}
                         close={this.closeModalHandler}
                         title={i18n.order_now_cta}>
                      <Form isSaved={this.state.resettedForm} />
                  </Modal>

    const fullCart = <React.Fragment>
                       {modal}
                       <div className="row">
                          <div className="col-12"><h2>{i18n.cart}</h2><hr /></div>
                          <div className="col-12 pb-3">{prodotti}</div>
                          <div className="col-12 pb-3 text-center">{button}</div>
                          <div className="col-12 pt-3">
                            <div className="alert alert-secondary">
                              {i18n.alert_more_than_one_call}
                            </div>
                          </div>
                        </div>
                      </React.Fragment>
    const emptyCart = <div className="text-center">
                        <p>{i18n.empty_cart}</p>
                        <p><Link to={path.home} className="btn btn-success">{i18n.home}</Link></p>
                      </div>
    return (
      <div className="p-cart">
        { cartIsFull ? fullCart : emptyCart}
      </div>
    )
  }
}
Cart.contextType = i18nContext
export default withTemplate(Cart)
