import React, { Component } from 'react'
import { f_cur } from "helpers/formatter"

export default class Price extends Component {
  render(){
    const { prod } = this.props
    let currency = f_cur(prod.currency);
    return (
      <React.Fragment>
        <span>{prod.price}</span>
        <span>{currency}</span>
      </React.Fragment>
    )
  }
}
