class Input extends Component {
  static contextType = i18nContext;
  render(){
    let i18n = this.context;
    const label = this.props.label?this.props.placeholder:this.props.placeholder?this.props.placeholder:i18n.form[name]?i18n.form[name]:this.props.name;
    return (
      <React.Fragment>
        {!this.props.hidelabel && <label className="text-capitalize">{label}</label>}
        <input {...this.props}
               className={"form-control"+f_ifexist(this.props.className)}
               type={this.props.type?this.props.type:"text"}
               placeholder={label}
               id={this.props.id?this.props.id:this.props.name} />
      </React.Fragment>
    )
  }
}
Input.contextType = i18nContext
