export const getPath = (lang)=>{
  const basePath = '/'
  const langPath = lang ? lang+'/' : ':lang?/'
  let objs = {
    home:     basePath,
    lang:     basePath+langPath,
    prods:    basePath+langPath+'prods',
    prodbase: basePath+langPath+'prod',
    prod_slug:basePath+langPath+'prod/:slug/:id/:trackid?',
    prod:     basePath+langPath+'prod/:id/:slug/:trackid?',
  }
 return objs;
}

export const getProdLink = (lang,prod)=>{
  let path = getPath(lang)
  //let prodPath = path.prodbase+'/'+prod.name+'/'+prod.id;
  let prodPath = path.prodbase+'/'+prod.id+'/'+prod.name;
  let prodLink = {pathname: prodPath, state: prod.id };
 return prodLink;
}

export const getProdLinkImg = (lang,prod)=>{
  const { REACT_APP_STAGE } = process.env
  const imgLink = ( REACT_APP_STAGE === 'dev' ? 'http://dev.offerprive.it' : '' )+"/shop/imgs/"
  //const imgLink = REACT_APP_IMGS // TODO va riabilitata REACT_APP_IMGS nell'env
  let prodImg = prod.code ? imgLink+"prods/"+prod.slug : '';
 return prodImg;
}

