import project from './project'

const initialState = {
  project: {
    loading: true,
    name: "Projectree",
    links: [{
        name: 'Site',
        link: 'www.projectree.cloud',
        description: 'My site!'
    }],
    customer: {
      name: 'Myself SPA',
      contacts: [{
        name: 'Mario Rossi',
        email: 'mariorossi@myselfspa.com',
        phone: '55512345',
        role: 'Account',
        note: 'Lorem ipsum',
        alternative_contact: 'Luigi Bianchi', // name of another customer's contact if is not avaiable
      }],
    },
    team: [{
      name: '',
      email: '',
      phone: '',
      role: '',
      note: '',
      leader: 0, // boolean, only one in a project
    }],
    documentation: [{
      folder: {
        name: '',
        content: [{
          document: {
            name: '',
            content: '', // text saved in db, alternative to "link"
            link: '', // link to the file, alternative to "content"
          }
        }]
      }
    }]
  },
}

export default function app(state = initialState, action) {
  return {
    project: project(state.project, action),
  }
}
