import project from './project'

const initialState = {
  project: {
    loading: true,
    name: "Projectree",
    links: [{
        name: 'Site',
        link: 'www.projectree.cloud',
        description: 'My site!'
    }],
    customer: {
      name: 'Myself SPA',
      contacts: [{
        name: '',
        email: '',
        phone: '',
        role: '',
        note: '',
      }]

    }
  },
}

export default function app(state = initialState, action) {
  return {
    project: project(state.project, action),
  }
}
