import './style.scss';
import React, { Component } from 'react'
import { connect } from "react-redux";
import { Link } from "react-router-dom";
//import Nav from './Nav'
import { getPath } from "helpers/getPath"
import {i18nContext} from 'contexts/i18n';

///const Header = (props) => {
class Header extends Component {
  static contextType = i18nContext;
  render() {
    let i18n = this.context;
    const { sitename } = this.props
    let path = getPath( i18n.lang )
    return (
      <nav className="t-header navbar sticky-top navbar-expand navbar-dark bg-dark">
        <div className="container">
          <Link className="navbar-brand btn btn-link pl-0 pl-md-3 font-weight-normal" to={path.lang}>
            <i className="fas fa-chess-queen mr-1"></i> {sitename}
          </Link>
          <button className="navbar-toggler"
                  type="button"
                  data-toggle="collapse"
                  data-target="#navbarSupportedContent"
                  aria-controls="navbarSupportedContent"
                  aria-expanded="false"
                  aria-label="Toggle navigation">
            <span className="navbar-toggler-icon">
            </span>
          </button>
          <div className="collapse-X X-navbar-collapse" id="navbarSupportedContent">
          {/*<Nav
            listClass="navbar-nav ml-auto mr-2"
            elemsClass="nav-item"
            linksClass="nav-link"
          />*/}
          </div>
        </div>
      </nav>
    )
  }
}

Header.contextType = i18nContext

const mapStateToProps = (state) => {
  const { sitename } = state
  return {
    sitename
  }
}

export default connect(mapStateToProps)(Header);
