import categories from './categories'
import prods from './prods'
import prod from './prod'
import cart from './cart'
import user from './user'
import tracker from './tracker'
import i18n from './i18n'

import {i18nObj} from 'contexts/i18n';

// Cerco la lingua del browser e la imposto come default, ma SOLO se esiste nell'i18nObj.
const langBrowser = ( navigator.language || navigator.userLanguage )
const controlLang = ( langBrowser ? langBrowser.substring(0,2) : false )
const currLang = controlLang && i18nObj[controlLang]!==undefined ? controlLang : 'en'

const initialState = {
  currency: "€",
  tracker: {},
  categories: {
    loading: true,
    list: []
  },
  prods: {
    loading: true,
    list: [],
    error: false
  },
  prod: {
    loading: true,
    list: [],
    error: false
  },
  cart: {
    list: [],
    submitted: false
  },
  user: {
    info: []
  },
  i18n: {
    lang: currLang
  }
}

export default function app(state = initialState, action) {
  return {
    currency: state.currency,
    i18n: i18n(state.i18n, action),
    tracker: tracker(state.tracker, action),
    categories: categories(state.categories, action),
    prods: prods(state.prods, action),
    prod: prod(state.prod, action),
    cart: cart(state.cart, action),
    user: user(state.user, action),
  }
}
