import React, { Component } from 'react'
import { connect } from "react-redux"
import {i18nContext} from 'contexts/i18n';
import { fetchUser, setCartSubmit, sendCartOrder/*, fetchCart*/ } from 'state/actions'
import { Form, Input, Submit } from "components/Form/";

class FormUser extends Component {
  static contextType = i18nContext;
  constructor(props) {
    super(props)
    this.state = {
      info: this.props.user.info,
      prods: this.props.cart.list
    }
  }
  handleChange = (event) => {
    const target = event.target;
    const value = (target.type === 'checkbox' || target.type === 'radio') ? target.checked : target.value;
    this.setState({ info:{ ...this.state.info, [target.name]: value } });
    this.props.dispatch( setCartSubmit( false ) )
  }
  handleSubmit = (event) => {
    event.preventDefault();
    this.props.dispatch( fetchUser( this.state.info ) )
    this.processCart()
  }
  processCart = () => {
    // TODO: queste azioni devono essere una callback
    // della chiamata ajax di scrittura ordine multiplo
    const { onSubmit } = this.props
    this.props.dispatch( setCartSubmit( true ) )
    // SVUOTA IL CART
    //this.props.dispatch( fetchCart( [] ) )
    onSubmit() // chiude la modale (se la form è compresa nella modale)

    // manda le info
    //( TODO: mettere le altre azioni come callback di questa appena funziona)
    this.props.dispatch( sendCartOrder( this.state.info,  ) )

  }
  render(){
    const i18n = this.context
    const { cart } = this.props
    const { form } = i18n
    const fields = [{name:'name'},
                    {name:'phone'},
                    {name:'address'},
                    {name:'zipcode',cols:'col-5'},
                    {name:'city',cols:'col-7'},
                    {name:'email'}]
    const inputs = fields.map((field, i) => (
                      <Input {...field} key={i}
                             defaultValue={this.state.info[field.name]}
                             onChange={this.handleChange} /> ));

    const thanks = <p>FORM INVIATA!</p>
    const fillform = <Form onSubmit={this.handleSubmit}>
                       {inputs}
                       <Submit value={form.send} /> {/* TODO: rimettere disabled={cart.submitted} */}
                     </Form>
    return (
      <div className="p-cart__form">
        { !this.state.isSubmitted ? fillform : thanks }
      </div>
    )
  }
}
FormUser.contextType = i18nContext
const mapStateToProps = (state) => {
  const { cart, user } = state
  return { cart, user }
}
export default connect(mapStateToProps)( FormUser );
