import './style.scss';
import React, { Component } from 'react'
import { f_ifexist, f_ucfirst } from "helpers/formatter"
import {i18nContext} from 'contexts/i18n';

class Input extends Component {
  static contextType = i18nContext;
  render(){
    let i18n = this.context;
    let { form } = i18n
    let { label, placeholder, name, className, type, id, cols } = this.props
    const fieldName = placeholder ? placeholder : label
                            ? label : form[name]
                            ? form[name] : name;
    return (
      <div className={"c-input__wrapper form-group"+f_ifexist(cols,'col-12')}>
        {label && <label className="c-label">{f_ucfirst(fieldName)}</label>}
        <input {...this.props}
               className={"c-input form-control"+f_ifexist(className)}
               type={type?type:"text"}
               placeholder={fieldName}
               id={id?id:name} />
      </div>
    )
  }
}

const Form = (props) => {
  return (
    <form {...props} className={"c-form"+f_ifexist(props.className)} >
    <div className="row">{props.children}</div>
    </form>
  )
}

class Submit extends Component {
  static contextType = i18nContext;
  render(){
    const i18n = this.context;
    const { form } = i18n
    const { value, className } = this.props
    return (
      <div className="c-submit form-group col mt-2">
        <input {...this.props}
               type="submit"
               value={value? value:form.submit}
               className={"btn btn-success btn-lg d-block float-none m-auto"
                          + f_ifexist(className)} />
      </div>
    )
  }
}

Input.contextType = i18nContext
export { Form, Input, Submit }
