import React, { Component } from 'react'
import {i18nContext} from 'contexts/i18n';
import { getPath } from "helpers/getPath"
import withTemplate from 'templates/'
import { Full } from 'pages/Prods/Lists/Row'
import Actions from 'pages/Cart/actions'
import Link from "components/Link/";

class Cart extends Component {
  static contextType = i18nContext;
  render(){
    let path = getPath()
    let i18n = this.context;
    const Item = Full
    const { cart } = this.props
    const prodotti = cart.list && cart.list.map((prod,index) => (<Item prod={prod} key={index} index={index}>
                                                                   <Actions prod={prod} key={index} index={index} />
                                                                 </Item>))
    const button = <button className="btn btn-success btn-lg d-inline-block">{i18n.order_now_cta}</button>

    const fullCart = <div className="row">
                        <div className="col-12"><h2>{i18n.cart}</h2><hr /></div>
                        <div className="col-12 pb-3">{prodotti}</div>
                        <div className="col-12 pb-3 text-center">{button}</div>
                        <div className="col-12 pt-3">
                          <div className="alert alert-secondary">
                            {i18n.alert_more_than_one_call}
                          </div>
                        </div>
                      </div>
    const emptyCart = <React.Fragment>
                        <p><Link to={path.home} className="btn btn-success">{i18n.home}</Link></p>
                        <p>{i18n.empty_cart}</p>
                      </React.Fragment>

    return (
      <div className="p-cart">
        {cart.list.length > 0 ? fullCart : emptyCart}
      </div>
    )
  }
}
Cart.contextType = i18nContext
export default withTemplate(Cart)
