import axios from 'axios'

const { REACT_APP_API } = process.env

export function setLoading(status) {
    return {
      type: 'SET_LOADING',
      status
    }
}

export function setLang(lang) {
  return {
    type: 'SET_LANG',
    payload: {
      lang
    }
  }
}

export function setTracker(tracker) {
  return {
    type: 'SET_TRACKID',
    payload: {
      tracker
    }
  }
}

export function fetchProds(lang,cat) {
  const langQuery = ( lang !== undefined && lang !== '' ) ? '&lang='+lang : ''
  const catQuery = ( cat !== undefined && cat !== '' ) ? '&cat='+cat : ''
  const apiUrl = REACT_APP_API+'product/read.php?'+langQuery+catQuery;
  return (dispatch, getState) => {
    axios.get(apiUrl).then((resp) => {
        dispatch({
            type: 'SET_PRODS',
            payload: {
              list: resp.data.records,
              error: resp.data.error,
              loading: false
            }
        });
    }, error => {
      dispatch({
          type: 'SET_PRODS',
          payload: {
              list: [],
              error: 'error',
              loading: false
          }
      });
    })
  };
}

export function fetchProd(prodId) {
  const apiUrl = REACT_APP_API+'product/read_one.php?id='+prodId;
  return (dispatch, getState) => {
    axios.get(apiUrl).then((resp) => {
        dispatch({
            type: 'SET_PROD',
            payload: {
              list: resp.data.record,
              error: resp.data.error,
              loading: false
            }
        })
    }, error => {
      dispatch({
          type: 'SET_PROD',
          payload: {
              list: [],
              error: 'error',
              loading: false
          }
      })
    })
  }
}

export function fetchCategs() {
  const apiUrl = REACT_APP_API+'category/read.php';
  return (dispatch, getState) => {
    axios.get(apiUrl).then((resp) => {
        dispatch({
            type: 'SET_CATEGORIES_LIST',
            payload: {
              list: resp.data.records
            }
        })
    }, error => {
      dispatch({
          type: 'SET_CATEGORIES_LIST',
          payload: {
              list: []
          }
      })
    })
  }
}

export function fetchCart(list) {
  return (dispatch, getState) => {
    dispatch({
        type: 'SET_CART',
        payload: {
          list
        }
    })
  }
}

export function addToCart(list) {
  return (dispatch, getState) => {
    dispatch({
        type: 'ADD_TO_CART',
        payload: {
          list
        }
    })
  }
}

export function removeFromCartById(list) {
  return (dispatch, getState) => {
    dispatch({
        type: 'REMOVE_FROM_CART_BY_ID',
        payload: {
          list
        }
    })
  }
}

export function removeFromCartByIndex(list,index) {
  return (dispatch, getState) => {
    dispatch({
        type: 'REMOVE_FROM_CART_BY_INDEX',
        payload: {
          list,
          index
        }
    })
  }
}

export function sendCartOrder(list,index) {
  return (dispatch, getState) => {
    dispatch({
        type: 'SEND_CART_ORDER',
        payload: {
          list,
          index
        }
    })
  }
}

export function setCartSubmit(submitted) {
  return (dispatch, getState) => {
    dispatch({
        type: 'SET_SUBMIT_STATUS',
        payload: {
          submitted
        }
    })
  }
}

export function fetchUser(info) {
  return (dispatch, getState) => {
    dispatch({
        type: 'SET_USER',
        payload: {
          info
        }
    })
  }
}
