import './style.scss';
import React, { Component } from 'react';
import { connect } from "react-redux";
import {i18nContext} from 'contexts/i18n';


import Link from "components/Link/";

class Banner extends Component {
  static contextType = i18nContext;
  render() {
    let i18n = this.context;

    const { id, title, link, align, children, img } = this.props
    const titleBox = title && <h1 className="display-4">{title}</h1>
    const ctaBox = <Link className="btn btn-lg mt-4" to={link}>go_to_prods</Link>
    const childBox = children && <div className="mt-4">{children}</div>
    const imgUrl = process.env.REACT_APP_IMGS+"/categories/"+img+".jpg"


    return (
      <div className={"c-banner c-banner--with-img d-flex align-items-center jumbotron jumbotron-fluid py-4 text-"+align} id={id} >
        <div className="container">
          {titleBox}
          {childBox}
          {ctaBox}
        </div>
        <div className='c-banner__bkg img-bkg'
        style={{backgroundImage:"url("+imgUrl+")"}}
        ></div>
      </div>
    )
  }
}

Banner.contextType = i18nContext

Banner.contextType = i18nContext
const mapStateToProps = (state) => {
  const { categories } = state
  return {
    loading: categories.loading,
    categories: categories.list
  }
}
export default connect(mapStateToProps)( (Banner) );
