import axios from 'axios'
import { encodeUrlParams } from "helpers/querystring"

const { REACT_APP_API, REACT_APP_FORM } = process.env

export function setLoading(status) {
    return {
      type: 'SET_LOADING',
      status
    }
}

export function setLang(lang) {
  return {
    type: 'SET_LANG',
    payload: {
      lang
    }
  }
}

export function fetchProds(lang,cat) {
  const langQuery = ( lang !== undefined && lang !== '' ) ? '&lang='+lang : ''
  const catQuery = ( cat !== undefined && cat !== '' ) ? '&cat='+cat : ''
  const apiUrl = REACT_APP_API+'product/read.php?'+langQuery+catQuery;
  return (dispatch, getState) => {
    axios.get(apiUrl).then((resp) => {
        dispatch({
            type: 'SET_PRODS',
            payload: {
              list: resp.data.records,
              error: resp.data.error,
              loading: false
            }
        });
    }, error => {
      dispatch({
          type: 'SET_PRODS',
          payload: {
              list: [],
              error: 'error',
              loading: false
          }
      });
    })
  };
}
