import React, { Component } from 'react'
import { connect } from "react-redux"
import {i18nContext} from 'contexts/i18n';
import { fetchUser } from 'state/actions'

import { Input } from "components/Form/";

class Form extends Component {
  static contextType = i18nContext;

  constructor(props) {
    super(props)
    this.state = {
      name: '',
      phone: '',
      address: '',
      city: '',
      zipcode: '',
      email: '',
    }
  }

  handleChange = (event) => {
    const target = event.target;
    const value =
      target.type === 'checkbox'
        ? target.checked
        : target.value;
    const name = target.name;

    this.setState({
      [name]: value,
    });
  }

  handleSubmit = (event) => {
    console.log('E\' stato inserito un valore: ', this.state);
    event.preventDefault();
    this.props.dispatch( fetchUser( this.state.info ) )
  }

  render(){
    //const i18n = this.context
    const { user } = this.props
    const { info } = user
    console.log( 'user.info in in', info );
    return (
      <React.Fragment>
        METTERE QUI LA FORM

        <form onSubmit={this.handleSubmit}>
          <Input value={this.state.name} name="name" onChange={this.handleChange} />
          <Input value={this.state.phone} name="phone" onChange={this.handleChange} />
          <Input value={this.state.address} name="address" onChange={this.handleChange} />
          <Input value={this.state.city} name="city" onChange={this.handleChange} />
          <Input value={this.state.zipcode} name="zipcode" onChange={this.handleChange} />
          <Input value={this.state.email} name="email" onChange={this.handleChange} />
          <input type="submit" value="Submit" className="btn btn-success" />
        </form>

      </React.Fragment>
    )
  }
}

Form.contextType = i18nContext
const mapStateToProps = (state) => {
  const { cart, user } = state
  return { cart, user }
}
export default connect(mapStateToProps)( Form );
