import React from 'react'
import Icon from 'components/Icon/'

const MenuIcon = (props) => {
  return (
      <div className="nav-link dropdown-toggle" onClick={props.toggleDropdown}>
      let puppa = { (props.badgeNumber && props.badgeNumber.length > 0)
              && <span className="badge badge-success">{props.badgeNumber.length}</span>}

        <Icon {...props}
              icon={props.icon}
              text={props.children}
              iconStyle={( props.showMenu ? 'is-active' : '' )} />
      </div>
  )
}
export default MenuIcon

